public void resetVectors () {
L1x = -10.0 + 20.0*Math.random();
L2x = -10.0 + 20.0*Math.random();
L1y = -10.0 + 20.0*Math.random();
L2y = -10.0 + 20.0*Math.random();

L1 = Math.sqrt(L1x*L1x+L1y*L1y);
L2 = Math.sqrt(L2x*L2x+L2y*L2y);

theta1 = Math.atan2(L1y,L1x);
if (theta1 < 0.0) theta1 = theta1 + 2.0*Math.PI; 
theta2 = Math.atan2(L2y,L2x);
if (theta2 < 0.0) theta2 = theta2 + 2.0*Math.PI;

angle1 = theta1*180.0/Math.PI;
angle2 = theta2*180.0/Math.PI;
}

